con:config.script:
def os= context.expand('${#TestCase#os}')
if( os == "windows")
{
	log.info "0"
}
else if (os == "linux")
{
	log.info "1"
}
else
{
	log.info "not supported"
}

int max=context.expand('${#TestCase#max}').toInteger()
for(int index=0; index<=max; index++)
{
	log.info index
}

log.info context.getClass()

def  testCase=context.getTestCase()
log.info testCase.getLabel()
